import { db } from './db';
import { cases, employees } from '@shared/schema';
import { eq, sql } from 'drizzle-orm';

export async function importCases2024FromExcel(filePath: string, createdById: string) {
  try {
    // Usar dynamic import para XLSX no contexto ESM
    const { default: XLSX } = await import('xlsx');

    // Ler arquivo Excel
    const workbook = XLSX.readFile(filePath);
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];

    // Converter para JSON
    const data = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

    // Assumir que a primeira linha são os cabeçalhos
    const headers = data[0] as string[];
    const rows = data.slice(1) as any[][];

    console.log('Cabeçalhos encontrados:', headers);
    console.log('Total de linhas:', rows.length);

    const importedCases = [];
    let errors = [];
    let employeeNotFound = [];

    for (let i = 0; i < rows.length; i++) {
      const row = rows[i];
      if (!row || row.length === 0) continue;

      try {
        // Mapear dados da planilha com estrutura real identificada:
        // 0: Matricula, 1: Nome, 2: Processo, 3: Prazo da Entrega, 4: Audiencia, 5: Status, 6: Data da entrega, 7: RESULTADO
        const matricula = String(row[0] || '').trim();
        const nome = String(row[1] || '').trim();
        const processo = String(row[2] || '').trim();
        const prazoEntrega = row[3] ? parseExcelDate(row[3]) : null;
        const audiencia = row[4] ? parseExcelDate(row[4]) : null;
        const statusOriginal = String(row[5] || '').trim();
        const dataEntrega = row[6] ? parseExcelDate(row[6]) : null;
        const resultado = String(row[7] || '').trim();

        const processData = {
          clientName: nome, // Nome do funcionário
          processNumber: `${matricula}-2024`, // Usar matrícula como identificador único
          description: processo, // Descrição do processo
          status: 'concluido', // Todos os processos de 2024 já estão concluídos
          startDate: new Date('2024-01-01'), // Data início estimada
          dueDate: prazoEntrega, // Prazo de entrega original
          completedDate: dataEntrega || new Date('2024-12-31'), // Data de conclusão
          dataEntrega: dataEntrega || new Date('2024-12-31'), // Data de entrega
          tipoProcesso: 'trabalhista',
          observacoes: resultado ? `Matrícula: ${matricula}. Resultado: ${resultado}` : `Matrícula: ${matricula}`,
          createdById: createdById,
          assignedToId: createdById, // Admin que está importando
        };

        // Validar campos obrigatórios
        if (!nome || !matricula) {
          errors.push(`Linha ${i + 2}: Nome e matrícula são obrigatórios`);
          continue;
        }

        // Verificar se processo já existe
        const existing = await db.select().from(cases).where(eq(cases.processNumber, processData.processNumber));

        if (existing.length > 0) {
          console.log(`Processo já existe: ${processData.processNumber} - pulando`);
          continue;
        }

        // Tentar encontrar funcionário pelo nome para vincular
        let employeeId = null;
        try {
          const employeeMatches = await db.select()
            .from(employees)
            .where(sql`LOWER(nome) LIKE ${`%${processData.clientName.toLowerCase()}%`}`)
            .limit(1);

          if (employeeMatches.length > 0) {
            employeeId = employeeMatches[0].id;
            console.log(`Funcionário encontrado: ${processData.clientName} -> ${employeeMatches[0].nome}`);
          } else {
            employeeNotFound.push({
              clientName: processData.clientName,
              processNumber: processData.processNumber
            });
          }
        } catch (e) {
          console.log(`Erro ao buscar funcionário para ${processData.clientName}:`, e.message);
        }

        // Inserir novo processo
        const [newCase] = await db.insert(cases).values({
          ...processData,
          employeeId
        }).returning();

        console.log(`Processo importado: ${processData.processNumber} - ${processData.clientName}`);
        importedCases.push(newCase);

      } catch (error) {
        errors.push(`Linha ${i + 2}: ${error.message}`);
        console.error(`Erro na linha ${i + 2}:`, error);
      }
    }

    return {
      success: true,
      imported: importedCases.length,
      errors: errors,
      employeeNotFound: employeeNotFound.length,
      employeeNotFoundList: employeeNotFound,
      message: `Importados ${importedCases.length} processos de 2024 com ${errors.length} erros. ${employeeNotFound.length} funcionários não encontrados.`
    };

  } catch (error) {
    console.error('Erro ao importar processos 2024:', error);
    return {
      success: false,
      error: error.message,
      message: 'Erro ao processar arquivo Excel de processos 2024'
    };
  }
}

// Função para converter data do Excel para JavaScript Date
function parseExcelDate(excelDate: any): Date | null {
  if (!excelDate) return null;

  // Se já é uma data
  if (excelDate instanceof Date) return excelDate;

  // Se é um número (serial date do Excel)
  if (typeof excelDate === 'number') {
    // Excel usa 1900-01-01 como base (serial 1), mas precisa de ajustes
    // Para datas do Excel, usar a fórmula: (serial - 25569) * 86400 * 1000
    const millisecondsPerDay = 24 * 60 * 60 * 1000;
    const excelEpoch = new Date(1970, 0, 1).getTime() - (25569 * millisecondsPerDay);
    return new Date(excelEpoch + (excelDate * millisecondsPerDay));
  }

  // Se é string, tentar parsing
  if (typeof excelDate === 'string') {
    const parsed = new Date(excelDate);
    return isNaN(parsed.getTime()) ? null : parsed;
  }

  return null;
}

// Função para buscar processos por status e período
export async function getCases2024Stats() {
  try {
    const cases2024 = await db.select()
      .from(cases)
      .where(sql`EXTRACT(YEAR FROM created_at) = 2024 OR EXTRACT(YEAR FROM completed_date) = 2024`);

    const stats = {
      total: cases2024.length,
      concluidos: cases2024.filter(c => c.status === 'concluido').length,
      comFuncionario: cases2024.filter(c => c.employeeId).length,
      semFuncionario: cases2024.filter(c => !c.employeeId).length,
    };

    return stats;
  } catch (error) {
    console.error('Erro ao buscar estatísticas 2024:', error);
    return {
      total: 0,
      concluidos: 0,
      comFuncionario: 0,
      semFuncionario: 0
    };
  }
}

server/importEmployees.ts.content:
import XLSX from 'xlsx';
import * as fs from 'fs';
import { db } from './db';
import { employees, cases } from '@shared/schema';
import { eq, sql, isNull } from 'drizzle-orm';

export async function importEmployeesFromExcel(filePath: string) {
  try {
    // Ler arquivo Excel
    const fileBuffer = fs.readFileSync(filePath);
    const workbook = XLSX.read(fileBuffer, { type: 'buffer' });
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];

    // Converter para JSON
    const data = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

    // Assumir que a primeira linha são os cabeçalhos
    const headers = data[0] as string[];
    const rows = data.slice(1) as any[][];

    console.log('📊 Cabeçalhos Excel encontrados:', headers);
    console.log('📈 Total de linhas para processar:', rows.length);

    const importedEmployees = [];
    let errors = [];

    for (let i = 0; i < rows.length; i++) {
      const row = rows[i];
      if (!row || row.length === 0 || !row[0]) continue;

      try {
        // Mapear dados da planilha Excel com formato específico BASE FACILITIES
        const employee = {
          empresa: String(row[0] || 'BASE FACILITIES').trim(),
          nome: String(row[1] || '').trim(),
          matricula: String(row[2] || '').trim(),
          rg: String(row[3] || '').trim() || null,
          pis: String(row[4] || '').trim() || null,
          // Converter data Excel para formato ISO
          dataAdmissao: row[5] && typeof row[5] === 'number' ? 
            new Date(Math.round((row[5] - 25569) * 86400 * 1000)).toISOString().split('T')[0] : null,
          dataDemissao: row[6] && typeof row[6] === 'number' ? 
            new Date(Math.round((row[6] - 25569) * 86400 * 1000)).toISOString().split('T')[0] : null,
          salario: row[7] ? String(row[7]).replace(/[^\d.,]/g, '') : null,
          cargo: String(row[8] || '').trim() || null,
          centroCusto: String(row[9] || '').trim() || null,
          departamento: String(row[10] || '').trim() || null,
          status: row[6] ? 'demitido' : 'ativo',
          email: null,
          telefone: null,
          endereco: null
        };

        // Validar campos obrigatórios
        if (!employee.matricula || !employee.nome) {
          errors.push(`Linha ${i + 2}: Matrícula e nome são obrigatórios`);
          continue;
        }

        console.log(`🔄 Processando: ${employee.nome} (${employee.matricula})`);

        // Verificar se funcionário já existe
        const existing = await db.select().from(employees).where(eq(employees.matricula, employee.matricula));

        if (existing.length > 0) {
          // Atualizar funcionário existente
          await db.update(employees)
            .set({
              ...employee,
              updatedAt: new Date()
            })
            .where(eq(employees.matricula, employee.matricula));
          console.log(`Funcionário atualizado: ${employee.nome} (${employee.matricula})`);
        } else {
          // Inserir novo funcionário
          await db.insert(employees).values(employee);
          console.log(`Funcionário criado: ${employee.nome} (${employee.matricula})`);
        }

        importedEmployees.push(employee);

      } catch (error) {
        errors.push(`Linha ${i + 2}: ${error.message}`);
      }
    }

    return {
      success: true,
      imported: importedEmployees.length,
      errors: errors,
      message: `Importados ${importedEmployees.length} funcionários com ${errors.length} erros`
    };

  } catch (error) {
    console.error('Erro ao importar funcionários:', error);
    return {
      success: false,
      error: error.message,
      message: 'Erro ao processar arquivo Excel'
    };
  }
}

// Função para buscar funcionário por nome (busca inteligente)
export async function findEmployeeByName(name: string) {
  const searchTerm = `%${name.toLowerCase()}%`;

  const results = await db.select()
    .from(employees)
    .where(sql`LOWER(nome) LIKE ${searchTerm}`)
    .limit(10);

  return results;
}

// Função para conectar processos com funcionários
export async function linkCasesToEmployees() {
  try {
    // Buscar todos os casos sem funcionário vinculado
    const cases = await db.select().from(cases).where(isNull(cases.employeeId));

    let linked = 0;
    let notFound = [];

    for (const case_ of cases) {
      // Tentar encontrar funcionário pelo nome do cliente
      const employeeMatches = await findEmployeeByName(case_.clientName);

      if (employeeMatches.length > 0) {
        // Pegar o primeiro match (pode ser melhorado com fuzzy matching)
        const employee = employeeMatches[0];

        await db.update(cases)
          .set({ employeeId: employee.id })
          .where(eq(cases.id, case_.id));

        console.log(`Processo ${case_.processNumber} vinculado ao funcionário ${employee.nome}`);
        linked++;
      } else {
        notFound.push({
          processNumber: case_.processNumber,
          clientName: case_.clientName
        });
      }
    }

    return {
      success: true,
      linked,
      notFound: notFound.length,
      notFoundCases: notFound
    };

  } catch (error) {
    console.error('Erro ao vincular processos:', error);
    return {
      success: false,
      error: error.message
    };
  }
}

export async function importEmployeesFromCSV(filePath: string) {
  try {
    // Ler arquivo CSV
    const csvData = fs.readFileSync(filePath, 'utf8');
    const lines = csvData.split('\n').filter(line => line.trim());

    // Primeira linha são os cabeçalhos
    const headers = lines[0].split(',').map(h => h.trim());
    console.log('Cabeçalhos CSV encontrados:', headers);
    console.log('Total de linhas CSV:', lines.length - 1);

    const importedEmployees = [];
    let errors = [];

    for (let i = 1; i < lines.length; i++) {
      const line = lines[i];
      if (!line.trim()) continue;

      try {
        // Parse CSV line (handling commas in quotes)
        const values = line.split(',').map(v => v.trim().replace(/^"/, '').replace(/"$/, ''));

        // Mapear dados da planilha Base Facilities CSV
        // Empresa,Nome do Funcionário,Código do Funcionário,Número do RG,Número do PIS,Data Admissão,Data Demissão,Salário,Descrição do Cargo,Centro de Custo,Descrição do Custo
        const employee = {
          empresa: values[0] || 'BASE FACILITIES',
          nome: values[1]?.trim() || '',
          matricula: values[2]?.trim() || '',
          rg: values[3]?.trim() || null,
          pis: values[4]?.trim() || null,
          dataAdmissao: values[5] ? values[5] : null,
          dataDemissao: values[6]?.trim() || null,
          salario: values[7] ? values[7].replace(/[^\d.,]/g, '') : null,
          cargo: values[8]?.trim() || null,
          centroCusto: values[9]?.trim() || null,
          departamento: values[10]?.trim() || null,
          status: values[6]?.trim() ? 'demitido' : 'ativo', // Se tem data demissão = demitido
          email: null,
          telefone: null,
          endereco: null
        };

        // Validar campos obrigatórios
        if (!employee.matricula || !employee.nome) {
          errors.push(`Linha ${i + 1}: Matrícula e nome são obrigatórios`);
          continue;
        }

        // Verificar se funcionário já existe
        const existing = await db.select().from(employees).where(eq(employees.matricula, employee.matricula));

        if (existing.length > 0) {
          // Atualizar funcionário existente
          await db.update(employees)
            .set({
              ...employee,
              updatedAt: new Date()
            })
            .where(eq(employees.matricula, employee.matricula));
          console.log(`Funcionário atualizado: ${employee.nome} (${employee.matricula})`);
        } else {
          // Inserir novo funcionário
          await db.insert(employees).values(employee);
          console.log(`Funcionário criado: ${employee.nome} (${employee.matricula})`);
        }

        importedEmployees.push(employee);

      } catch (error) {
        errors.push(`Linha ${i + 1}: ${error.message}`);
      }
    }

    return {
      success: true,
      imported: importedEmployees.length,
      errors: errors,
      message: `Importados ${importedEmployees.length} funcionários com ${errors.length} erros`
    };

  } catch (error) {
    console.error('Erro ao importar funcionários CSV:', error);
    return {
      success: false,
      error: error.message,
      message: 'Erro ao processar arquivo CSV'
    };
  }
}

