import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCaseSchema } from "@shared/schema";
import { z } from "zod";

interface CaseModalProps {
  trigger: React.ReactNode;
  caseData?: any;
  onSubmit: (data: any) => void;
  isSubmitting?: boolean;
}

const formSchema = z.object({
  matricula: z.string().min(1, "Matrícula é obrigatória"),
  nome: z.string().min(1, "Nome é obrigatório"),
  processo: z.string().min(1, "Descrição do processo é obrigatória"),
  prazoEntrega: z.string().optional(),
  audiencia: z.string().optional(),
  observacao: z.string().optional(),
  status: z.string().default("novo"),
});

export default function CaseModal({ trigger, caseData, onSubmit, isSubmitting }: CaseModalProps) {
  const [open, setOpen] = useState(false);

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      matricula: caseData?.matricula || "",
      nome: caseData?.nome || "",
      processo: caseData?.processo || "",
      prazoEntrega: caseData?.prazoEntrega ? new Date(caseData.prazoEntrega).toISOString().split('T')[0] : "",
      audiencia: caseData?.audiencia ? new Date(caseData.audiencia).toISOString().split('T')[0] : "",
      observacao: caseData?.observacao || "",
      status: caseData?.status || "novo",
    },
  });

  const handleSubmit = (values: z.infer<typeof formSchema>) => {
    const submitData = {
      ...values,
      prazoEntrega: values.prazoEntrega ? new Date(values.prazoEntrega) : null,
      audiencia: values.audiencia ? new Date(values.audiencia) : null,
      observacao: values.observacao || null,
    };
    onSubmit(submitData);
    setOpen(false);
    form.reset();
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[525px]">
        <DialogHeader>
          <DialogTitle>
            {caseData ? "Editar Caso" : "Novo Caso"}
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="matricula"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Matrícula</FormLabel>
                  <FormControl>
                    <Input placeholder="Ex: 1500258" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="nome"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Nome</FormLabel>
                  <FormControl>
                    <Input placeholder="Nome completo" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="processo"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Descrição do Processo</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Descreva os processos separados por vírgula (ex: TRABALHISTA, Rescisão indireta, Dano Moral)"
                      className="min-h-[80px]"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="prazoEntrega"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Prazo de Entrega</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="audiencia"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Data Audiência</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="observacao"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Observação</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Observações adicionais sobre o processo (opcional)"
                      className="min-h-[60px]"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setOpen(false)}
              >
                Cancelar
              </Button>
              <Button type="submit" disabled={isSubmitting}>
                {isSubmitting ? "Salvando..." : (caseData ? "Salvar" : "Criar")}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ConfirmStatusDialo.content:
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface ConfirmStatusDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onConfirm: () => void;
  currentStatus: string;
  newStatus: string;
  processNumber: string;
  clientName: string;
}

export default function ConfirmStatusDialog({
  open,
  onOpenChange,
  onConfirm,
  currentStatus,
  newStatus,
  processNumber,
  clientName,
}: ConfirmStatusDialogProps) {
  const statusLabels = {
    'novo': 'Novo',
    'andamento': 'Em Andamento',
    'concluido': 'Concluído',
    'pendente': 'Pendente'
  };

  const currentLabel = statusLabels[currentStatus as keyof typeof statusLabels];
  const newLabel = statusLabels[newStatus as keyof typeof statusLabels];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'novo': return 'text-yellow-600';
      case 'andamento': return 'text-blue-600';
      case 'concluido': return 'text-green-600';
      case 'pendente': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  return (
    <AlertDialog open={open} onOpenChange={onOpenChange}>
      <AlertDialogContent className="max-w-md">
        <AlertDialogHeader>
          <AlertDialogTitle className="text-lg font-semibold">
            Confirmar Alteração de Status
          </AlertDialogTitle>
          <AlertDialogDescription className="text-sm text-gray-600 space-y-3">
            <div className="bg-gray-50 p-3 rounded-lg border">
              <p><strong>Processo:</strong> {processNumber}</p>
              <p><strong>Cliente:</strong> {clientName}</p>
            </div>

            <div className="flex items-center justify-center space-x-4 py-2">
              <div className="text-center">
                <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium bg-gray-100 ${getStatusColor(currentStatus)}`}>
                  {currentLabel}
                </span>
                <p className="text-xs text-gray-500 mt-1">Status atual</p>
              </div>

              <div className="text-gray-400">→</div>

              <div className="text-center">
                <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium bg-gray-100 ${getStatusColor(newStatus)}`}>
                  {newLabel}
                </span>
                <p className="text-xs text-gray-500 mt-1">Novo status</p>
              </div>
            </div>

            <p className="text-center text-sm">
              Tem certeza que deseja alterar o status deste processo?
            </p>
          </AlertDialogDescription>
        </AlertDialogHeader>

        <AlertDialogFooter className="gap-2">
          <AlertDialogCancel className="bg-gray-100 hover:bg-gray-200 text-gray-800">
            Cancelar
          </AlertDialogCancel>
          <AlertDialogAction 
            onClick={onConfirm}
            className={`${
              newStatus === 'concluido' 
                ? 'bg-green-600 hover:bg-green-700' 
                : newStatus === 'andamento'
                ? 'bg-blue-600 hover:bg-blue-700'
                : 'bg-gray-600 hover:bg-gray-700'
            } text-white`}
          >
            Confirmar Alteração
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

client/src/components/DeadlineAlert.tsx.content:
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, Clock, CheckCircle } from "lucide-react";

interface DeadlineAlertProps {
  prazoEntrega: string | null;
  status: string;
}

export default function DeadlineAlert({ prazoEntrega, status }: DeadlineAlertProps) {
  if (!prazoEntrega || status === 'concluido') {
    return status === 'concluido' ? (
      <Badge className="bg-green-100 text-green-800">
        <CheckCircle size={12} className="mr-1" />
        Concluído
      </Badge>
    ) : (
      <span className="text-gray-500 text-sm">-</span>
    );
  }

  const today = new Date();
  const deadline = new Date(prazoEntrega);
  const diffTime = deadline.getTime() - today.getTime();
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  // Cores baseadas na proximidade do prazo
  if (diffDays < 0) {
    return (
      <Badge className="bg-red-100 text-red-800 animate-pulse">
        <AlertTriangle size={12} className="mr-1" />
        Atrasado ({Math.abs(diffDays)}d)
      </Badge>
    );
  } else if (diffDays === 0) {
    return (
      <Badge className="bg-red-100 text-red-800 animate-pulse">
        <AlertTriangle size={12} className="mr-1" />
        Hoje
      </Badge>
    );
  } else if (diffDays === 1) {
    return (
      <Badge className="bg-orange-100 text-orange-800">
        <Clock size={12} className="mr-1" />
        Amanhã
      </Badge>
    );
  } else if (diffDays <= 3) {
    return (
      <Badge className="bg-orange-100 text-orange-800">
        <Clock size={12} className="mr-1" />
        {diffDays} dias
      </Badge>
    );
  } else if (diffDays <= 7) {
    return (
      <Badge className="bg-yellow-100 text-yellow-800">
        <Clock size={12} className="mr-1" />
        {diffDays} dias
      </Badge>
    );
  } else {
    return (
      <Badge className="bg-green-100 text-green-800">
        <Clock size={12} className="mr-1" />
        {diffDays} dias
      </Badge>
    );
  }
}

client/src/components/DocumentManager.ts.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { 
  FileText, 
  Upload, 
  Link2, 
  Check, 
  X, 
  Plus,
  ExternalLink,
  Folder,
  Clock
} from "lucide-react";

interface DocumentManagerProps {
  caseId: string;
  tipoProcesso?: string;
  documentosSolicitados?: string[];
  documentosAnexados?: any[];
  onUpdateDocuments?: (data: any) => void;
}

export default function DocumentManager({ 
  caseId, 
  tipoProcesso, 
  documentosSolicitados = [], 
  documentosAnexados = [],
  onUpdateDocuments 
}: DocumentManagerProps) {
  const [newDocumentName, setNewDocumentName] = useState("");
  const [newDocumentLink, setNewDocumentLink] = useState("");
  const [observacoes, setObservacoes] = useState("");
  const [selectedDocuments, setSelectedDocuments] = useState<string[]>([]);

  // Documentos padrão por tipo de processo
  const documentosPadrao: Record<string, string[]> = {
    'Trabalhista': [
      'Carteira de Trabalho',
      'Comprovante de Residência',
      'RG',
      'CPF',
      'Comprovantes de Pagamento',
      'Declaração de Testemunhas'
    ],
    'Rescisão Indireta': [
      'Carta de Rescisão',
      'Testemunhas',
      'E-mails/Mensagens',
      'Atestados Médicos',
      'Comprovantes de Falta de Pagamento',
      'Registro de Ponto'
    ],
    'Dano Moral': [
      'Boletim de Ocorrência',
      'Laudos Médicos',
      'Testemunhas',
      'Fotos/Vídeos',
      'Comprovantes de Gastos',
      'Correspondências'
    ],
    'Previdenciário': [
      'Documentos Pessoais',
      'CNIS',
      'Carteira de Trabalho',
      'Laudos Médicos',
      'Comprovantes de Contribuição'
    ],
    'Cível': [
      'Contratos',
      'Comprovantes',
      'Correspondências',
      'Documentos Pessoais'
    ]
  };

  const documentosNecessarios = tipoProcesso ? documentosPadrao[tipoProcesso] || [] : [];
  const documentosCustom = documentosSolicitados.filter(doc => !documentosNecessarios.includes(doc));

  const handleAddCustomDocument = () => {
    if (newDocumentName.trim()) {
      const updatedDocs = [...documentosSolicitados, newDocumentName.trim()];
      onUpdateDocuments?.({
        documentosSolicitados: updatedDocs
      });
      setNewDocumentName("");
    }
  };

  const handleAttachDocument = () => {
    if (newDocumentLink.trim()) {
      const newAttachment = {
        nome: newDocumentName.trim() || "Documento Anexado",
        link: newDocumentLink.trim(),
        dataAnexo: new Date().toISOString(),
        tipo: "link"
      };

      const updatedAttachments = [...documentosAnexados, newAttachment];
      onUpdateDocuments?.({
        documentosAnexados: updatedAttachments,
        observacoes
      });

      setNewDocumentName("");
      setNewDocumentLink("");
    }
  };

  const getDocumentStatus = (docName: string) => {
    const isAttached = documentosAnexados.some(doc => 
      doc.nome?.toLowerCase().includes(docName.toLowerCase())
    );
    return isAttached;
  };

  return (
    <div className="space-y-6">
      {/* Documentos Solicitados */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center text-gray-800">
            <FileText className="mr-2 h-5 w-5" />
            Documentos Necessários
            {tipoProcesso && (
              <Badge variant="outline" className="ml-2">
                {tipoProcesso}
              </Badge>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Documentos Padrão */}
          {documentosNecessarios.length > 0 && (
            <div>
              <h4 className="text-sm font-semibold text-gray-700 mb-3">Documentos Padrão</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                {documentosNecessarios.map((doc, index) => {
                  const isAttached = getDocumentStatus(doc);
                  return (
                    <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          checked={isAttached}
                          disabled
                          className={isAttached ? "data-[state=checked]:bg-green-500" : ""}
                        />
                        <span className={`text-sm ${isAttached ? 'text-green-700 font-medium' : 'text-gray-700'}`}>
                          {doc}
                        </span>
                      </div>
                      {isAttached ? (
                        <Check className="w-4 h-4 text-green-500" />
                      ) : (
                        <Clock className="w-4 h-4 text-orange-500" />
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {/* Documentos Customizados */}
          {documentosCustom.length > 0 && (
            <div>
              <h4 className="text-sm font-semibold text-gray-700 mb-3">Documentos Específicos</h4>
              <div className="space-y-2">
                {documentosCustom.map((doc, index) => {
                  const isAttached = getDocumentStatus(doc);
                  return (
                    <div key={index} className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <Checkbox checked={isAttached} disabled />
                        <span className={`text-sm ${isAttached ? 'text-blue-700 font-medium' : 'text-blue-600'}`}>
                          {doc}
                        </span>
                      </div>
                      {isAttached ? (
                        <Check className="w-4 h-4 text-green-500" />
                      ) : (
                        <Clock className="w-4 h-4 text-orange-500" />
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {/* Adicionar Documento Customizado */}
          <div className="border-t pt-4">
            <h4 className="text-sm font-semibold text-gray-700 mb-3">Adicionar Documento Específico</h4>
            <div className="flex space-x-2">
              <Input
                placeholder="Nome do documento"
                value={newDocumentName}
                onChange={(e) => setNewDocumentName(e.target.value)}
                className="flex-1"
              />
              <Button onClick={handleAddCustomDocument} size="sm">
                <Plus className="w-4 h-4 mr-1" />
                Adicionar
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Anexar Documentos */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center text-gray-800">
            <Upload className="mr-2 h-5 w-5" />
            Anexar Documentos
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="flex items-start space-x-3">
              <Folder className="w-5 h-5 text-blue-600 mt-1" />
              <div className="flex-1">
                <h4 className="text-sm font-semibold text-blue-800">Organização no Drive</h4>
                <p className="text-xs text-blue-600 mt-1">
                  Recomendamos criar uma pasta específica para este processo no seu Google Drive ou outro serviço de nuvem.
                  Estrutura sugerida: Processos/{tipoProcesso || 'Geral'}/{caseId}
                </p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label className="text-sm font-medium">Nome do Documento</Label>
              <Input
                placeholder="Ex: Carteira de Trabalho - João Silva"
                value={newDocumentName}
                onChange={(e) => setNewDocumentName(e.target.value)}
              />
            </div>
            <div>
              <Label className="text-sm font-medium">Link do Documento</Label>
              <Input
                placeholder="Ex: https://drive.google.com/file/..."
                value={newDocumentLink}
                onChange={(e) => setNewDocumentLink(e.target.value)}
              />
            </div>
          </div>

          <div>
            <Label className="text-sm font-medium">Observações</Label>
            <Textarea
              placeholder="Observações sobre os documentos..."
              value={observacoes}
              onChange={(e) => setObservacoes(e.target.value)}
              rows={3}
            />
          </div>

          <Button onClick={handleAttachDocument} className="w-full">
            <Link2 className="w-4 h-4 mr-2" />
            Anexar Link do Documento
          </Button>
        </CardContent>
      </Card>

      {/* Documentos Anexados */}
      {documentosAnexados.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center text-gray-800">
              <Check className="mr-2 h-5 w-5 text-green-600" />
              Documentos Anexados ({documentosAnexados.length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {documentosAnexados.map((doc, index) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg hover:bg-gray-50">
                  <div className="flex items-center space-x-3">
                    <FileText className="w-4 h-4 text-blue-600" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">{doc.nome}</p>
                      <p className="text-xs text-gray-500">
                        Anexado em {new Date(doc.dataAnexo).toLocaleDateString('pt-BR')}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant="outline" className="bg-green-50 text-green-700">
                      Anexado
                    </Badge>
                    <Button size="sm" variant="outline" asChild>
                      <a href={doc.link} target="_blank" rel="noopener noreferrer">
                        <ExternalLink className="w-3 h-3" />
                      </a>
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/EmployeeSearchModa.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Search, User, Building, MapPin, Calendar, DollarSign, Plus } from "lucide-react";
import type { Employee as EmployeeType } from "@shared/schema";

interface EmployeeSearchModalProps {
  onSelectEmployee: (employee: { codigo: string; nomeCompleto: string }) => void;
  trigger?: React.ReactNode;
}

export default function EmployeeSearchModal({ onSelectEmployee, trigger }: EmployeeSearchModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchType, setSearchType] = useState<"nome" | "codigo" | "rg" | "pis">("nome");

  // Buscar funcionários reais da API
  const { data: allEmployees, isLoading } = useQuery({
    queryKey: ["/api/employees"],
    queryFn: async () => {
      const response = await fetch(`/api/employees`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json() as Promise<EmployeeType[]>;
    },
  });

  // Filtrar funcionários excluindo deletados e aplicando busca
  const filteredEmployees = (allEmployees || []).filter(emp => {
    // Não mostrar funcionários deletados
    if (emp.status === 'deletado') return false;

    if (!searchTerm) return true;

    const searchLower = searchTerm.toLowerCase();
    switch (searchType) {
      case "nome":
        return emp.nome?.toLowerCase().includes(searchLower);
      case "codigo":
        return emp.matricula?.toString().toLowerCase().includes(searchLower);
      case "rg":
        return emp.rg?.toString().toLowerCase().includes(searchLower);
      case "pis":
        return emp.pis?.toString().toLowerCase().includes(searchLower);
      default:
        return true;
    }
  });

  const handleSelectEmployee = (employee: EmployeeType) => {
    onSelectEmployee({
      codigo: employee.matricula || '',
      nomeCompleto: employee.nome || ''
    });
    setIsOpen(false);
    setSearchTerm("");
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button variant="outline" size="sm">
            <Search className="h-4 w-4 mr-2" />
            Buscar Funcionário
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Buscar Funcionário na Base de Dados
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Filtros de busca */}
          <div className="flex gap-4">
            <div className="flex-1">
              <Input
                placeholder={`Buscar por ${searchType === 'nome' ? 'nome' : searchType === 'codigo' ? 'código' : searchType === 'rg' ? 'RG' : 'PIS'}...`}
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full"
              />
            </div>
            <div className="flex gap-2">
              <Button
                variant={searchType === "nome" ? "default" : "outline"} 
                size="sm"
                onClick={() => setSearchType("nome")}
              >
                Nome
              </Button>
              <Button
                variant={searchType === "codigo" ? "default" : "outline"} 
                size="sm"
                onClick={() => setSearchType("codigo")}
              >
                Código
              </Button>
              <Button
                variant={searchType === "rg" ? "default" : "outline"} 
                size="sm"
                onClick={() => setSearchType("rg")}
              >
                RG
              </Button>
              <Button
                variant={searchType === "pis" ? "default" : "outline"} 
                size="sm"
                onClick={() => setSearchType("pis")}
              >
                PIS
              </Button>
            </div>
          </div>

          {/* Resultados da busca */}
          <div className="border rounded-lg max-h-96 overflow-y-auto">
            <Table>
              <TableHeader className="bg-gray-50 sticky top-0">
                <TableRow>
                  <TableHead>Código</TableHead>
                  <TableHead>Nome Completo</TableHead>
                  <TableHead>Cargo</TableHead>
                  <TableHead>Centro de Custo</TableHead>
                  <TableHead>Admissão</TableHead>
                  <TableHead>Ações</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8">
                      <div className="flex items-center justify-center space-x-2">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
                        <span>Carregando funcionários...</span>
                      </div>
                    </TableCell>
                  </TableRow>
                ) : filteredEmployees.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8 text-gray-500">
                      {searchTerm ? "Nenhum funcionário encontrado com os critérios de busca." : "Nenhum funcionário encontrado."}
                      <br />
                      <span className="text-sm">Tente buscar por nome, código, RG ou PIS.</span>
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredEmployees.map((employee) => (
                    <TableRow key={employee.id} className="hover:bg-gray-50">
                      <TableCell className="font-mono font-medium">
                        {employee.matricula}
                      </TableCell>
                      <TableCell className="font-medium">
                        {employee.nome}
                      </TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          <div className="text-sm">{employee.cargo || "-"}</div>
                          {employee.salario && (
                            <Badge variant="outline" className="text-xs">
                              <DollarSign className="h-3 w-3 mr-1" />
                              R$ {Number(employee.salario).toLocaleString('pt-BR')}
                            </Badge>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          <div className="text-sm font-medium">{employee.centroCusto || "-"}</div>
                          <div className="text-xs text-gray-500">{employee.departamento || "-"}</div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-1 text-sm">
                          <Calendar className="h-3 w-3" />
                          {employee.dataAdmissao ? new Date(employee.dataAdmissao).toLocaleDateString('pt-BR') : "-"}
                        </div>
                      </TableCell>
                      <TableCell>
                        <Button
                          size="sm"
                          onClick={() => handleSelectEmployee(employee)}
                          className="bg-blue-600 hover:bg-blue-700"
                        >
                          Selecionar
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>

          {/* Info sobre a base de dados */}
          <div className="bg-blue-50 p-3 rounded-lg">
            <div className="flex items-center justify-between">
              <div>
                <div className="flex items-center gap-2 text-blue-700 text-sm">
                  <Building className="h-4 w-4" />
                  <span className="font-medium">Base de Dados:</span>
                  <span>{allEmployees?.length || 0} funcionários cadastrados</span>
                </div>
                <p className="text-xs text-blue-600 mt-1">
                  Busque por nome completo, código funcional, RG ou número do PIS para localizar rapidamente.
                </p>
              </div>
              <Button 
                onClick={() => {
                  setIsOpen(false);
                  // Navegar para página de funcionários para criar novo
                  window.location.href = '/employees';
                }}
                className="bg-green-600 hover:bg-green-700 text-white"
                size="sm"
              >
                <Plus className="h-4 w-4 mr-2" />
                Cadastrar Novo
              </Button>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ImportCases2024Mod.content:
import { useState } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { FileSpreadsheet, Upload, CheckCircle, AlertTriangle, Users, Calendar } from "lucide-react";

interface ImportStats {
  total: number;
  concluidos: number;
  comFuncionario: number;
  semFuncionario: number;
}

interface ImportResult {
  success: boolean;
  imported: number;
  errors: string[];
  employeeNotFound: number;
  employeeNotFoundList: Array<{ clientName: string; processNumber: string }>;
  message: string;
}

export default function ImportCases2024Modal() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [importResult, setImportResult] = useState<ImportResult | null>(null);

  // Buscar estatísticas atuais dos processos 2024
  const { data: stats, isLoading: statsLoading } = useQuery<ImportStats>({
    queryKey: ["/api/cases/stats-2024"],
  });

  const importMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/cases/import-2024");
      return await response.json();
    },
    onSuccess: (result: ImportResult) => {
      setImportResult(result);
      queryClient.invalidateQueries({ queryKey: ["/api/cases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/cases/stats-2024"] });

      if (result.success) {
        toast({
          title: "Importação Concluída",
          description: result.message,
        });
      } else {
        toast({
          title: "Erro na Importação",
          description: result.message,
          variant: "destructive",
        });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Erro",
        description: "Falha ao importar processos de 2024",
        variant: "destructive",
      });
      console.error("Import error:", error);
    },
  });

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <FileSpreadsheet className="mr-2 h-5 w-5 text-blue-600" />
            Importar Processos de 2024
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Estatísticas atuais */}
          <div>
            <h3 className="text-lg font-semibold mb-3 flex items-center">
              <Calendar className="mr-2 h-4 w-4" />
              Situação Atual dos Processos 2024
            </h3>
            {statsLoading ? (
              <div className="animate-pulse bg-gray-200 h-16 rounded"></div>
            ) : (
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <Card className="p-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-600">{stats?.total || 0}</div>
                    <div className="text-sm text-gray-600">Total</div>
                  </div>
                </Card>
                <Card className="p-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">{stats?.concluidos || 0}</div>
                    <div className="text-sm text-gray-600">Concluídos</div>
                  </div>
                </Card>
                <Card className="p-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">{stats?.comFuncionario || 0}</div>
                    <div className="text-sm text-gray-600">Vinculados</div>
                  </div>
                </Card>
                <Card className="p-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-orange-600">{stats?.semFuncionario || 0}</div>
                    <div className="text-sm text-gray-600">Sem Vínculo</div>
                  </div>
                </Card>
              </div>
            )}
          </div>

          <Separator />

          {/* Informações sobre a importação */}
          <div className="bg-blue-50 p-4 rounded-lg">
            <h3 className="font-semibold text-blue-800 mb-2">Sobre a Importação</h3>
            <ul className="text-sm text-blue-700 space-y-1">
              <li>• Arquivo: processos 2024_1753977488884.xlsx</li>
              <li>• Total de registros na planilha: 213 processos</li>
              <li>• Todos os processos serão marcados como "concluído"</li>
              <li>• Sistema tentará vincular funcionários automaticamente</li>
              <li>• Processos duplicados serão ignorados</li>
            </ul>
          </div>

          {/* Botão de importação */}
          <div className="text-center">
            <Button
              onClick={() => importMutation.mutate()}
              disabled={importMutation.isPending}
              size="lg"
              className="bg-green-600 hover:bg-green-700"
            >
              {importMutation.isPending ? (
                <div className="flex items-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Importando...
                </div>
              ) : (
                <div className="flex items-center">
                  <Upload className="mr-2 h-4 w-4" />
                  Importar Processos de 2024
                </div>
              )}
            </Button>
          </div>

          {/* Resultado da importação */}
          {importResult && (
            <div className="mt-6">
              <Separator className="mb-4" />
              <div className={`p-4 rounded-lg ${importResult.success ? 'bg-green-50' : 'bg-red-50'}`}>
                <div className="flex items-center mb-3">
                  {importResult.success ? (
                    <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                  ) : (
                    <AlertTriangle className="h-5 w-5 text-red-600 mr-2" />
                  )}
                  <h3 className={`font-semibold ${importResult.success ? 'text-green-800' : 'text-red-800'}`}>
                    Resultado da Importação
                  </h3>
                </div>

                <p className={`mb-4 ${importResult.success ? 'text-green-700' : 'text-red-700'}`}>
                  {importResult.message}
                </p>

                {importResult.success && (
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-4">
                    <div className="text-center">
                      <Badge variant="outline" className="text-green-600 border-green-600">
                        {importResult.imported} Importados
                      </Badge>
                    </div>
                    <div className="text-center">
                      <Badge variant="outline" className="text-red-600 border-red-600">
                        {importResult.errors.length} Erros
                      </Badge>
                    </div>
                    <div className="text-center">
                      <Badge variant="outline" className="text-orange-600 border-orange-600">
                        {importResult.employeeNotFound} Sem Funcionário
                      </Badge>
                    </div>
                  </div>
                )}

                {/* Lista de erros */}
                {importResult.errors.length > 0 && (
                  <div className="mt-4">
                    <h4 className="font-medium text-red-800 mb-2">Erros Encontrados:</h4>
                    <div className="max-h-32 overflow-y-auto bg-red-100 p-3 rounded text-sm">
                      {importResult.errors.map((error, index) => (
                        <div key={index} className="text-red-700">{error}</div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Lista de funcionários não encontrados */}
                {importResult.employeeNotFoundList && importResult.employeeNotFoundList.length > 0 && (
                  <div className="mt-4">
                    <h4 className="font-medium text-orange-800 mb-2">Funcionários Não Encontrados:</h4>
                    <div className="max-h-32 overflow-y-auto bg-orange-100 p-3 rounded text-sm">
                      {importResult.employeeNotFoundList.slice(0, 10).map((item, index) => (
                        <div key={index} className="text-orange-700">
                          {item.clientName} - Processo: {item.processNumber}
                        </div>
                      ))}
                      {importResult.employeeNotFoundList.length > 10 && (
                        <div className="text-orange-600 font-medium">
                          ... e mais {importResult.employeeNotFoundList.length - 10} registros
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/Layout.tsx.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Bell, BarChart3, FileText, History, UserCog, LogOut, Menu, X, Users, Check } from "lucide-react";
import facilityLogo from "@assets/449265_6886_1753882292906.jpg";

interface LayoutProps {
  children: React.ReactNode;
}

export default function Layout({ children }: LayoutProps) {
  const [location] = useLocation();
  const { user } = useAuth();
  const [currentTime, setCurrentTime] = useState(new Date());
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [notificationsRead, setNotificationsRead] = useState(false);

  // Buscar notificações recentes (últimos logs)
  const { data: recentLogs } = useQuery({
    queryKey: ["/api/activity-logs", { limit: 5, processOnly: true }],
    queryFn: async () => {
      const response = await fetch(`/api/activity-logs?limit=5&processOnly=true`, {
        credentials: 'include',
      });
      if (!response.ok) return [];
      return await response.json();
    },
    refetchInterval: 10000, // Atualizar a cada 10 segundos
    enabled: !!user,
  });

  // Update time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  const handleLogout = () => {
    // Clear all cached data
    localStorage.clear();
    sessionStorage.clear();

    // Force logout and redirect
    fetch("/api/logout", {
      method: "POST",
      credentials: "include"
    }).then(() => {
      window.location.href = "/auth";
    }).catch(() => {
      window.location.href = "/auth";
    });
  };

  // Função para verificar se tem permissão para uma página
  const hasPagePermission = (page: string) => {
    if (user?.role === 'admin') return true;
    return (user as any)?.permissions?.pages?.[page] === true;
  };

  const navItems = [
    ...(hasPagePermission('dashboard') ? [
      { path: "/", icon: BarChart3, label: "Dashboard", description: "Visão geral e métricas" }
    ] : []),
    ...(hasPagePermission('cases') ? [
      { path: "/cases", icon: FileText, label: "Processos", description: "Gerenciar processos jurídicos" }
    ] : []),
    ...(hasPagePermission('activityLog') ? [
      { path: "/activity-log", icon: History, label: "Atividades", description: "Log de ações do sistema" }
    ] : []),
    ...(hasPagePermission('users') ? [
      { path: "/users", icon: UserCog, label: "Usuários", description: "Gerenciar usuários" }
    ] : []),
  ].filter(Boolean);

  const getPageTitle = () => {
    const titles: Record<string, string> = {
      "/": "Dashboard - Visão Geral",
      "/cases": "Gerenciamento de Processos",
      "/employees": "Base de Funcionários",
      "/activity-log": "Log de Atividades",
      "/users": "Gerenciamento de Usuários",
    };
    return titles[location] || "Dashboard";
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-black bg-opacity-50 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Navigation Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-72 bg-gradient-to-b from-slate-900 to-slate-800 shadow-2xl transform transition-transform duration-300 ease-in-out ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0`}>

        {/* Mobile Close Button */}
        <div className="flex justify-end p-4 lg:hidden">
          <Button
            variant="ghost"
            size="sm"
            className="text-slate-300 hover:bg-slate-700"
            onClick={() => setSidebarOpen(false)}
          >
            <X size={20} />
          </Button>
        </div>

        {/* User Info */}
        <div className="p-6 border-b border-slate-700 mt-4">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center">
              <span className="text-white font-semibold text-lg">
                {user?.firstName?.charAt(0) || 'U'}
              </span>
            </div>
            <div>
              <div className="text-white font-medium">{user?.firstName} {user?.lastName}</div>
              <div className="text-slate-400 text-sm capitalize">{user?.role}</div>
              <div className="text-slate-500 text-xs">{user?.email}</div>
            </div>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 p-4 space-y-2">
          {navItems.map((item) => {
            const Icon = item.icon;
            const isActive = location === item.path;

            return (
              <Link key={item.path} href={item.path}>
                <div className={`group flex items-center space-x-3 px-4 py-3 rounded-xl transition-all duration-200 cursor-pointer ${
                  isActive 
                    ? 'bg-gradient-to-r from-blue-600 to-blue-700 text-white shadow-lg' 
                    : 'text-slate-300 hover:bg-slate-700/50 hover:text-white'
                }`}>
                  <Icon size={20} className={`${isActive ? 'text-white' : 'text-slate-400 group-hover:text-white'}`} />
                  <div className="flex-1">
                    <div className="font-medium">{item.label}</div>
                    <div className={`text-xs ${isActive ? 'text-blue-100' : 'text-slate-500 group-hover:text-slate-400'}`}>
                      {item.description}
                    </div>
                  </div>
                </div>
              </Link>
            );
          })}
        </nav>

        {/* Footer */}
        <div className="p-4 border-t border-slate-700">
          <Button
            onClick={handleLogout}
            variant="ghost"
            className="w-full flex items-center justify-center space-x-2 text-slate-300 hover:text-white hover:bg-slate-700/50 py-3"
          >
            <LogOut size={18} />
            <span>Sair do Sistema</span>
          </Button>

          <div className="mt-4 pt-4 border-t border-slate-700">
            <div className="text-center text-xs text-slate-500">
              © 2024 BASE FACILITIES
            </div>
            <div className="text-center text-xs text-slate-600 mt-1">
              {currentTime.toLocaleString('pt-BR')}
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-72 flex-1">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 h-16 flex items-center justify-between px-6 sticky top-0 z-30">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="sm"
              className="lg:hidden"
              onClick={() => setSidebarOpen(true)}
            >
              <Menu size={20} />
            </Button>
            <h1 className="text-xl font-semibold text-gray-900">
              {getPageTitle()}
            </h1>
          </div>

          <div className="flex items-center space-x-4">
            <Popover>
              <PopoverTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  className="relative p-2 text-gray-400 hover:text-gray-600"
                  onClick={() => setNotificationsRead(true)}
                >
                  <Bell size={20} />
                  {!notificationsRead && recentLogs && recentLogs.length > 0 && (
                    <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {recentLogs.length}
                    </span>
                  )}
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-80 p-0" align="end">
                <div className="p-4 border-b">
                  <div className="flex items-center justify-between">
                    <h3 className="font-medium">Atividades Recentes</h3>
                    <Button 
                      variant="ghost" 
                      size="sm"
                      onClick={() => setNotificationsRead(true)}
                      className="text-xs"
                    >
                      <Check className="h-3 w-3 mr-1" />
                      Marcar como lida
                    </Button>
                  </div>
                </div>
                <div className="max-h-80 overflow-y-auto">
                  {recentLogs && recentLogs.length > 0 ? (
                    recentLogs.map((log: any, index: number) => (
                      <div key={index} className="p-3 border-b last:border-b-0 hover:bg-gray-50">
                        <div className="text-sm font-medium text-gray-900 mb-1">
                          {log.action?.replace(/_/g, ' ')}
                        </div>
                        <div className="text-xs text-gray-600 mb-1">
                          {log.description}
                        </div>
                        <div className="text-xs text-gray-400">
                          {new Date(log.timestamp).toLocaleString('pt-BR')}
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="p-4 text-center text-gray-500 text-sm">
                      Nenhuma atividade recente
                    </div>
                  )}
                </div>
                <div className="p-3 border-t">
                  <Link href="/activity-log">
                    <Button variant="outline" size="sm" className="w-full">
                      Ver todas as atividades
                    </Button>
                  </Link>
                </div>
              </PopoverContent>
            </Popover>
            <div className="hidden md:block text-sm text-gray-600">
              {currentTime.toLocaleString('pt-BR')}
            </div>
          </div>
        </header>

        {/* Page Content */}
        <main className="p-6">
          {children}
        </main>
      </div>
    </div>
  );
}

client/src/components/NewCaseModal.tsx.content:
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { CaseWithRelations } from "@shared/schema";
import { useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Search } from "lucide-react";
import * as React from "react";

interface NewCaseModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: any) => void;
  isSubmitting?: boolean;
  caseData?: CaseWithRelations | null;
}

const formSchema = z.object({
  matricula: z.string().min(1, "Matrícula é obrigatória"),
  clientName: z.string().min(1, "Nome do cliente é obrigatório"),
  processType: z.string().min(1, "Tipo de processo é obrigatório"),
  dueDate: z.string().min(1, "Prazo de entrega é obrigatório"),
  audienceDate: z.string().optional(),
  observacoes: z.string().optional(),
});

type FormSchema = z.infer<typeof formSchema>;

export default function NewCaseModal({ isOpen, onClose, onSubmit, isSubmitting, caseData }: NewCaseModalProps) {
  const { toast } = useToast();
  const [isSearchingEmployee, setIsSearchingEmployee] = React.useState(false);

  const form = useForm<FormSchema>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      matricula: caseData?.matricula || "",
      clientName: caseData?.clientName || "",
      processType: caseData?.processNumber || "",
      dueDate: caseData?.dueDate ? new Date(caseData.dueDate).toISOString().split('T')[0] : "",
      audienceDate: caseData?.dataAudiencia ? new Date(caseData.dataAudiencia).toISOString().split('T')[0] : "",
      observacoes: caseData?.observacoes || "",
    },
  });

  // Query para buscar funcionários
  const { data: employees } = useQuery({
    queryKey: ["/api/employees"],
    queryFn: async () => {
      const response = await fetch('/api/employees', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch employees');
      return response.json();
    },
  });

  // Função para buscar funcionário por matrícula
  const handleMatriculaSearch = React.useCallback(async (matricula: string) => {
    if (!matricula || matricula.length < 3) return;

    setIsSearchingEmployee(true);
    try {
      const employee = employees?.find((emp: any) => 
        emp.matricula && emp.matricula.toLowerCase().includes(matricula.toLowerCase())
      );

      if (employee) {
        form.setValue('clientName', employee.nome || '');
        toast({
          title: "Funcionário encontrado",
          description: `${employee.nome} - Matrícula: ${employee.matricula}`,
        });
      } else {
        toast({
          title: "Funcionário não encontrado",
          description: "Verifique a matrícula e tente novamente",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error('Erro ao buscar funcionário:', error);
    } finally {
      setIsSearchingEmployee(false);
    }
  }, [employees, form, toast]);

  // Watch matrícula field to trigger search
  const matriculaValue = form.watch('matricula');
  React.useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (matriculaValue && matriculaValue.length >= 3) {
        handleMatriculaSearch(matriculaValue);
      }
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [matriculaValue, handleMatriculaSearch]);

  // Resetar formulário quando caseData muda (para edição)
  React.useEffect(() => {
    if (caseData) {
      form.reset({
        matricula: (caseData as any).matricula || "",
        clientName: caseData.clientName || "",
        processType: caseData.processNumber || "",
        dueDate: caseData.dueDate ? new Date(caseData.dueDate).toISOString().split('T')[0] : "",
        audienceDate: caseData.dataAudiencia ? new Date(caseData.dataAudiencia).toISOString().split('T')[0] : "",
        observacoes: caseData.observacoes || "",
      });
    } else {
      form.reset({
        matricula: "",
        clientName: "",
        processType: "",
        dueDate: "",
        audienceDate: "",
        observacoes: "",
      });
    }
  }, [caseData, form]);

  const handleSubmit = (values: FormSchema) => {
    const submitData = {
      clientName: values.clientName,
      processNumber: values.processType, // Using processType as processNumber for backend compatibility
      description: values.processType || 'Processo Jurídico', // Required field
      dueDate: values.dueDate ? new Date(values.dueDate).toISOString() : null,
      dataAudiencia: values.audienceDate ? new Date(values.audienceDate).toISOString() : null,
      observacoes: values.observacoes || '',
      matricula: values.matricula,
      status: 'novo', // Default status
      createdById: 'admin-id' // This will be overridden by the server based on authenticated user
    };

    console.log('🚀 Sending data to API:', submitData);
    onSubmit(submitData);
    form.reset();
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-xl font-semibold text-gray-900">
            {caseData ? "Editar Processo" : "Novo Processo"}
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="matricula"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700">Matrícula</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          placeholder="Digite a matrícula do funcionário"
                          {...field}
                          className="border-gray-300 pr-10"
                        />
                        {isSearchingEmployee && (
                          <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                            <div className="w-4 h-4 border-2 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                          </div>
                        )}
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="clientName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700">Nome do Cliente</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Nome será preenchido automaticamente"
                        {...field}
                        className="border-gray-300 bg-gray-50"
                        readOnly
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="processType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-700">Tipo de Processo</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Ex: Trabalhista, Rescisão Contratual, Acordo Coletivo..."
                      {...field}
                      className="border-gray-300"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="dueDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700">Prazo de Entrega *</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        className="border-gray-300"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="audienceDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700">Data Audiência</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        className="border-gray-300"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="observacoes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-700">Observações</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Detalhes adicionais: valores envolvidos, situação atual, documentos necessários..."
                      className="border-gray-300 min-h-[80px] resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-3 pt-6 border-t">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleClose}
                disabled={isSubmitting}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                disabled={isSubmitting}
                className="bg-blue-600 hover:bg-blue-700"
              >
                {isSubmitting ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Salvando...
                  </div>
                ) : (
                  caseData ? "Atualizar Processo" : "Criar Processo"
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ProcessTagRenderer.content:
import { Badge } from "@/components/ui/badge";

interface ProcessTagRendererProps {
  processo: string;
}

export default function ProcessTagRenderer({ processo }: ProcessTagRendererProps) {
  const processos = processo.split(',').map(p => p.trim()).filter(p => p.length > 0);

  return (
    <div className="flex flex-wrap gap-1 max-w-sm">
      {processos.map((proc, index) => (
        <Badge 
          key={index} 
          variant="secondary" 
          className="text-xs bg-blue-100 text-blue-800 hover:bg-blue-200 transition-colors whitespace-nowrap"
        >
          {proc}
        </Badge>
      ))}
    </div>
  );
}

client/src/components/ProcessTypeChart.t.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { BarChart3 } from "lucide-react";
import { BarChart, Bar, XAxis, YAxis, ResponsiveContainer, Tooltip, Cell } from 'recharts';

interface ProcessTypeChartProps {
  cases: any[];
}

export default function ProcessTypeChart({ cases }: ProcessTypeChartProps) {
  // Contar tipos de processos
  const processCounts = cases.reduce((acc, caseData) => {
    const tipo = caseData.tipoProcesso || 'Não Classificado';
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  // Cores para cada tipo
  const colors: Record<string, string> = {
    'Trabalhista': '#ef4444',
    'Rescisão Indireta': '#f97316',
    'Dano Moral': '#8b5cf6',
    'Previdenciário': '#06b6d4',
    'Cível': '#10b981',
    'Não Classificado': '#6b7280'
  };

  const chartData = Object.entries(processCounts).map(([name, value]) => ({
    name,
    value,
    fill: colors[name] || '#6b7280'
  }));

  return (
    <Card className="shadow-lg">
      <CardHeader>
        <CardTitle className="flex items-center text-gray-800">
          <BarChart3 className="mr-2 h-5 w-5" />
          Tipos de Processos Mais Comuns
        </CardTitle>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <XAxis 
              dataKey="name" 
              angle={-45} 
              textAnchor="end" 
              height={80} 
              fontSize={12}
              interval={0}
            />
            <YAxis />
            <Tooltip />
            <Bar dataKey="value">
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.fill} />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
}

