import { useState } from "react";
import React from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import type { User } from "@shared/schema";
import { Shield, Eye, Edit } from "lucide-react";

const userSchema = z.object({
  username: z.string().min(1, "Nome de usuário é obrigatório").optional().or(z.literal("")),
  email: z.string().email("Email inválido").optional().or(z.literal("")),
  firstName: z.string().optional().or(z.literal("")),
  lastName: z.string().optional().or(z.literal("")),
  password: z.string().optional().or(z.literal("")),
  role: z.enum(["admin", "editor", "viewer"]),
  permissions: z.object({
    matricula: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    nome: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    processo: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    prazoEntrega: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    audiencia: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    status: z.object({
      view: z.boolean().optional().default(false),
      edit: z.boolean().optional().default(false),
    }).optional(),
    pages: z.object({
      dashboard: z.boolean().optional().default(false),
      cases: z.boolean().optional().default(false),
      users: z.boolean().optional().default(false),
      activityLog: z.boolean().optional().default(false),
    }).optional(),
    canCreateCases: z.boolean().optional().default(false),
    canDeleteCases: z.boolean().optional().default(false),
  }).optional(),
});

type UserForm = z.infer<typeof userSchema>;

interface UserModalProps {
  user?: User | null;
  onSubmit: (data: any) => void;
  onClose: () => void;
  isSubmitting?: boolean;
}

const defaultPermissions = {
  matricula: { view: true, edit: false },
  nome: { view: true, edit: false },
  processo: { view: true, edit: false },
  prazoEntrega: { view: true, edit: false },
  audiencia: { view: true, edit: false },
  status: { view: true, edit: false },
  observacao: { view: true, edit: false },
  canCreateCases: false,
  canDeleteCases: false,
  pages: {
    dashboard: true,
    cases: true,
    activityLog: false,
    users: false
  }
};

export default function UserModal({ user, onSubmit, onClose, isSubmitting }: UserModalProps) {
  const [showPassword, setShowPassword] = useState(false);

  const form = useForm<UserForm>({
    resolver: zodResolver(userSchema),
    defaultValues: {
      username: user?.username || "",
      email: user?.email || "",
      firstName: user?.firstName || "",
      lastName: user?.lastName || "",
      password: "",
      role: (user?.role as "admin" | "editor" | "viewer") || "viewer",
      permissions: user ? (user as any)?.permissions : defaultPermissions,
    },
  });

  // Recarregar valores quando o usuário muda
  React.useEffect(() => {
    if (user) {
      const userPermissions = (user as any)?.permissions;
      console.log('🔄 CARREGANDO PERMISSÕES DO USUÁRIO:', user.username, userPermissions);

      form.reset({
        username: user.username || "",
        email: user.email || "",
        firstName: user.firstName || "",
        lastName: user.lastName || "",
        password: "",
        role: (user.role as "admin" | "editor" | "viewer") || "viewer",
        permissions: userPermissions || defaultPermissions,
      });
    } else {
      // Para novo usuário, usar permissões padrão
      form.reset({
        username: "",
        email: "",
        firstName: "",
        lastName: "",
        password: "",
        role: "viewer",
        permissions: defaultPermissions,
      });
    }
  }, [user, form]);

  const handleSubmit = (values: UserForm) => {
