import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ArrowUp, ArrowDown, FileText, CheckCircle, Clock, Timer, TrendingUp, Users, AlertTriangle } from "lucide-react";

export default function Dashboard() {
  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/dashboard/stats"],
  });

  const { data: recentLogs, isLoading: logsLoading } = useQuery({
    queryKey: ["/api/activity-logs"],
  });

  const { data: cases, isLoading: casesLoading } = useQuery({
    queryKey: ["/api/cases"],
  });

  if (statsLoading || logsLoading || casesLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <Skeleton className="h-4 w-32 mb-2" />
                <Skeleton className="h-8 w-16 mb-4" />
                <Skeleton className="h-4 w-24" />
              </CardContent>
            </Card>
          ))}
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Skeleton className="h-64" />
          <Skeleton className="h-64" />
        </div>
      </div>
    );
  }

  const statCards = [
    {
      title: "Total de Processos",
      value: stats?.total || "0",
      icon: FileText,
      color: "bg-blue-500",
      trend: "+12%",
      trendUp: true,
    },
    {
      title: "Processos Concluídos",
      value: stats?.completed || "0",
      icon: CheckCircle,
      color: "bg-green-500",
      trend: "+8%",
      trendUp: true,
    },
    {
      title: "Em Andamento",
      value: stats?.inProgress || "0",
      icon: Clock,
      color: "bg-yellow-500",
      trend: "+3%",
      trendUp: true,
    },
    {
      title: "Tempo Médio",
      value: `${stats?.averageResponseTime || 0}d`,
      icon: Timer,
      color: "bg-purple-500",
      trend: "-2d",
      trendUp: false,
    },
  ];

  const recentCases = cases?.slice(0, 5) || [];
  const urgentCases = cases?.filter((c: any) => {
    if (c.dueDate) {
      const dueDate = new Date(c.dueDate);
      const today = new Date();
      const diffTime = dueDate.getTime() - today.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      return diffDays <= 7 && diffDays >= 0;
    }
    return false;
  }) || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'novo': return 'bg-blue-100 text-blue-800';
      case 'andamento': return 'bg-yellow-100 text-yellow-800';
      case 'concluido': return 'bg-green-100 text-green-800';
      case 'pendente': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="border-b border-gray-200 pb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Dashboard Executivo</h1>
        <p className="text-gray-600">Visão geral dos processos jurídicos e métricas de desempenho</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index} className="relative overflow-hidden">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600 mb-1">{stat.title}</p>
                    <p className="text-3xl font-bold text-gray-900">{stat.value}</p>
                    <div className="flex items-center mt-2">
                      {stat.trendUp ? (
                        <ArrowUp className="w-4 h-4 text-green-500 mr-1" />
                      ) : (
                        <ArrowDown className="w-4 h-4 text-red-500 mr-1" />
                      )}
                      <span className={`text-sm font-medium ${stat.trendUp ? 'text-green-600' : 'text-red-600'}`}>
                        {stat.trend}
                      </span>
                      <span className="text-sm text-gray-500 ml-1">vs mês anterior</span>
                    </div>
                  </div>
                  <div className={`w-12 h-12 ${stat.color} rounded-lg flex items-center justify-center`}>
                    <Icon className="w-6 h-6 text-white" />
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Charts and Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Processos Urgentes */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold flex items-center">
              <AlertTriangle className="w-5 h-5 text-orange-500 mr-2" />
              Processos Urgentes
            </CardTitle>
            <Badge variant="destructive">{urgentCases.length}</Badge>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {urgentCases.length > 0 ? urgentCases.map((caseItem: any) => {
                const dueDate = new Date(caseItem.dueDate);
                const today = new Date();
                const diffTime = dueDate.getTime() - today.getTime();
                const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

                return (
                  <div key={caseItem.id} className="flex items-center justify-between p-3 bg-orange-50 rounded-lg border border-orange-200">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-900">{caseItem.clientName}</h4>
                      <p className="text-sm text-gray-600 truncate">{caseItem.description}</p>
                    </div>
                    <div className="text-right">
                      <Badge className={`${diffDays <= 2 ? 'bg-red-100 text-red-800' : 'bg-orange-100 text-orange-800'}`}>
                        {diffDays === 0 ? 'Hoje' : diffDays === 1 ? 'Amanhã' : `${diffDays}d`}
                      </Badge>
                    </div>
                  </div>
                );
              }) : (
                <div className="text-center py-8 text-gray-500">
                  <AlertTriangle className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                  <p>Nenhum processo urgente no momento</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Atividade Recente */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold flex items-center">
              <TrendingUp className="w-5 h-5 text-blue-500 mr-2" />
              Atividade Recente
            </CardTitle>
            <Button variant="outline" size="sm">Ver Todas</Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {recentLogs && recentLogs.length > 0 ? recentLogs.slice(0, 5).map((log: any, index: number) => (
                <div key={index} className="flex items-start space-x-3 p-3 hover:bg-gray-50 rounded-lg transition-colors">
                  <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
                  <div className="flex-1">
                    <p className="text-sm text-gray-900">{log.description}</p>
                    <p className="text-xs text-gray-500 mt-1">
                      {log.user?.firstName} {log.user?.lastName} • {new Date(log.createdAt).toLocaleString('pt-BR')}
                    </p>
                  </div>
                </div>
              )) : (
                <div className="text-center py-8 text-gray-500">
                  <TrendingUp className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                  <p>Nenhuma atividade recente</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Processos Recentes */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle className="text-lg font-semibold flex items-center">
            <FileText className="w-5 h-5 text-green-500 mr-2" />
            Processos Recentes
          </CardTitle>
          <Button variant="outline" size="sm">Ver Todos</Button>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {recentCases.length > 0 ? recentCases.map((caseItem: any) => (
              <div key={caseItem.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors">
                <div className="flex-1">
                  <div className="flex items-center space-x-3">
                    <h4 className="font-medium text-gray-900">{caseItem.clientName}</h4>
                    <Badge className={getStatusColor(caseItem.status)}>
                      {caseItem.status === 'novo' && 'Novo'}
                      {caseItem.status === 'andamento' && 'Em Andamento'}
                      {caseItem.status === 'concluido' && 'Concluído'}
                      {caseItem.status === 'pendente' && 'Pendente'}
                    </Badge>
                  </div>
                  <p className="text-sm text-gray-600 mt-1">{caseItem.description}</p>
                  <p className="text-xs text-gray-500 mt-1">
                    Criado em {new Date(caseItem.createdAt).toLocaleDateString('pt-BR')}
                  </p>
                </div>
                <Button variant="ghost" size="sm">
                  Ver Detalhes
                </Button>
              </div>
            )) : (
              <div className="text-center py-8 text-gray-500">
                <FileText className="w-12 h-12 text-gray-300 mx-auto mb-3" />
                <p>Nenhum processo encontrado</p>
                <Button className="mt-4" variant="outline">
                  Criar Primeiro Processo
                </Button>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Employees.tsx.content:
import { useState, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { toast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { 
  Search, 
  Filter,
  Upload, 
  User,
  Plus,
  Edit,
  Trash2,
  Download,
  X,
  ChevronDown,
  ChevronUp,
  Users
} from "lucide-react";
import type { Employee } from "@shared/schema";

export default function Employees() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    nome: "",
    matricula: "",
    cargo: "",
    departamento: "",
    empresa: "all"
  });

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [formData, setFormData] = useState({
    empresa: "2",
    nome: "",
    matricula: "",
    rg: "",
    pis: "",
    dataAdmissao: "",
    dataDemissao: "",
    salario: "",
    cargo: "",
    centroCusto: "",
    departamento: "",
    status: "ativo",
    email: "",
    telefone: "",
    endereco: ""
  });

  // Company options
  const companyOptions = [
    { value: "2", label: "2" },
    { value: "33", label: "33" },
    { value: "55", label: "55" },
    { value: "79", label: "79" },
    { value: "104", label: "104" },
    { value: "107", label: "107" },
    { value: "123", label: "123" },
    { value: "125", label: "125" },
    { value: "126", label: "126" },
    { value: "127", label: "127" },
    { value: "128", label: "128" },
    { value: "150", label: "150" }
  ];



  const { data: allEmployees, isLoading } = useQuery({
    queryKey: ["/api/employees"],
    queryFn: async () => {
      const response = await fetch(`/api/employees`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json() as Promise<Employee[]>;
    },
  });

  // Filtros funcionais melhorados - excluir deletados
  const filteredEmployees = useMemo(() => {
    if (!allEmployees) return [];

    console.log('🔍 FILTROS DEBUG:', {
      total: allEmployees.length,
      searchTerm,
      filters,
      firstEmployee: allEmployees[0]
    });

    const filtered = allEmployees.filter(emp => {
      // Não mostrar funcionários deletados
      if (emp.status === 'deletado') return false;

      // Busca geral (deve ser mais flexível e incluir mais campos)
      const matchesSearch = !searchTerm || 
        emp.nome?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.matricula?.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.rg?.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.pis?.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.cargo?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.departamento?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.empresa?.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.centroCusto?.toString().toLowerCase().includes(searchTerm.toLowerCase());

      // Filtros específicos
      const matchesFilters = 
        (!filters.nome || emp.nome?.toLowerCase().includes(filters.nome.toLowerCase())) &&
        (!filters.matricula || emp.matricula?.toString().toLowerCase().includes(filters.matricula.toLowerCase())) &&
        (!filters.cargo || emp.cargo?.toLowerCase().includes(filters.cargo.toLowerCase())) &&
        (!filters.departamento || emp.departamento?.toLowerCase().includes(filters.departamento.toLowerCase())) &&
        (!filters.empresa || filters.empresa === "all" || emp.empresa?.toString() === filters.empresa);

      const result = matchesSearch && matchesFilters;

      if (searchTerm && result) {
        console.log('✅ MATCH:', emp.nome, emp.matricula);
      }

      return result;
    });

    console.log('📊 FILTROS RESULTADO:', filtered.length, 'de', allEmployees.length);
    return filtered;
  }, [allEmployees, searchTerm, filters]);

  // Pagination logic
  const totalPages = Math.ceil(filteredEmployees.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const employees = filteredEmployees.slice(startIndex, endIndex);

  // Reset pagination when filters change
  const resetPagination = () => {
    setCurrentPage(1);
  };

  // Importar planilha
  const importMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/employees/import', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error(`Erro HTTP: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: (result) => {
      toast({
        title: "Importação concluída",
        description: `${result.imported} funcionários importados com ${result.errors?.length || 0} erros`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro na importação",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Criar funcionário
  const createEmployeeMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', '/api/employees', data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Funcionário criado", description: "Funcionário adicionado com sucesso!" });
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
      setIsCreateModalOpen(false);
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Erro", description: error.message, variant: "destructive" });
    }
  });

  // Atualizar funcionário
  const updateEmployeeMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest('PUT', `/api/employees/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Funcionário atualizado", description: "Dados salvos com sucesso!" });
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
      setIsEditModalOpen(false);
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Erro", description: error.message, variant: "destructive" });
    }
  });

  // Deletar funcionário
  const deleteEmployeeMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest('DELETE', `/api/employees/${id}`);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Funcionário excluído", description: "Funcionário removido com sucesso!" });
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
    },
    onError: (error: Error) => {
      toast({ title: "Erro", description: error.message, variant: "destructive" });
    }
  });

  // Funções auxiliares
  const resetForm = () => {
    setFormData({
      empresa: "2",
      nome: "",
      matricula: "",
      rg: "",
      pis: "",
      dataAdmissao: "",
      dataDemissao: "",
      salario: "",
      cargo: "",
      centroCusto: "",
      departamento: "",
      status: "ativo",
      email: "",
      telefone: "",
      endereco: ""
    });
  };

  const openEditModal = (employee: Employee) => {
    setSelectedEmployee(employee);
    setFormData({
      empresa: employee.empresa || "2",
      nome: employee.nome || "",
      matricula: employee.matricula || "",
      rg: employee.rg || "",
      pis: employee.pis || "",
      dataAdmissao: employee.dataAdmissao || "",
      dataDemissao: employee.dataDemissao || "",
      salario: employee.salario || "",
      cargo: employee.cargo || "",
      centroCusto: employee.centroCusto || "",
      departamento: employee.departamento || "",
      status: employee.status || "ativo",
      email: employee.email || "",
      telefone: employee.telefone || "",
      endereco: employee.endereco || ""
    });
    setIsEditModalOpen(true);
  };

  const handleSubmit = () => {
    if (selectedEmployee) {
      updateEmployeeMutation.mutate({ id: selectedEmployee.id, data: formData });
    } else {
      createEmployeeMutation.mutate(formData);
    }
  };

  const clearFilters = () => {
    setFilters({
      nome: "",
      matricula: "",
      cargo: "",
      departamento: "",
      empresa: "all"
    });
    setSearchTerm("");
    resetPagination();
  };

  const formatDate = (dateStr: string | null) => {
    if (!dateStr) return "-";
    return new Date(dateStr).toLocaleDateString('pt-BR');
  };

  const formatSalaryDisplay = (salary: string | null) => {
    if (!salary) return "-";
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(Number(salary));
  };

  // Handle salary input change with formatting
  const handleSalaryChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Remove everything except numbers, comma, and period
    const cleanValue = value.replace(/[^\d,.]/g, '');
    setFormData({ ...formData, salario: cleanValue });
  };

  const uploadFile = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      importMutation.mutate(file);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Carregando funcionários...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6">
      {/* Header Profissional */}
      <div className="bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-lg p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Users className="h-8 w-8" />
            <div>
              <h1 className="text-2xl font-bold">Gestão de Funcionários</h1>
              <p className="text-blue-100">BASE FACILITIES - Sistema Corporativo</p>
            </div>
          </div>
          <div className="text-right">
            <div className="text-2xl font-bold">{filteredEmployees.length}</div>
            <div className="text-blue-100">Total de Funcionários</div>
            <div className="text-sm text-blue-200 mt-1">
              Página {currentPage} de {totalPages}
            </div>
          </div>
        </div>
      </div>

      {/* Barra de Ações Principais */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Busca Global */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                placeholder="Buscar por nome, matrícula, RG, cargo ou departamento..."
                value={searchTerm}
                onChange={(e) => {
                  setSearchTerm(e.target.value);
                  resetPagination();
                }}
                className="pl-10 h-11 text-sm"
              />
            </div>

            {/* Botões de Ação */}
            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={() => setShowFilters(!showFilters)}
                className="h-11"
              >
                <Filter className="h-4 w-4 mr-2" />
                Filtros
                {showFilters ? <ChevronUp className="h-4 w-4 ml-2" /> : <ChevronDown className="h-4 w-4 ml-2" />}
              </Button>

              <Button
                variant="outline"
                onClick={() => setIsCreateModalOpen(true)}
                className="h-11"
              >
                <Plus className="h-4 w-4 mr-2" />
                Novo
              </Button>

              <Button
                variant="outline"
                className="h-11"
              >
                <label className="cursor-pointer flex items-center">
                  <Upload className="h-4 w-4 mr-2" />
                  Importar
                  <input
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={uploadFile}
                    className="hidden"
                    disabled={importMutation.isPending}
                  />
                </label>
              </Button>
            </div>
          </div>

          {/* Painel de Filtros Avançados */}
          {showFilters && (
            <div className="mt-4 p-4 bg-gray-50 rounded-lg border">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div>
                  <Label className="text-sm font-medium">Nome</Label>
                  <Input
                    placeholder="Filtrar por nome"
                    value={filters.nome}
                    onChange={(e) => {
                      setFilters({...filters, nome: e.target.value});
                      resetPagination();
                    }}
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium">Matrícula</Label>
                  <Input
                    placeholder="Filtrar por matrícula"
                    value={filters.matricula}
                    onChange={(e) => {
                      setFilters({...filters, matricula: e.target.value});
                      resetPagination();
                    }}
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium">Cargo</Label>
                  <Input
                    placeholder="Filtrar por cargo"
                    value={filters.cargo}
                    onChange={(e) => {
                      setFilters({...filters, cargo: e.target.value});
                      resetPagination();
                    }}
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium">Centro de Custo</Label>
                  <Input
                    placeholder="Filtrar por centro de custo"
                    value={filters.departamento}
                    onChange={(e) => {
                      setFilters({...filters, departamento: e.target.value});
                      resetPagination();
                    }}
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium">Empresa</Label>
                  <Select
                    value={filters.empresa}
                    onValueChange={(value) => {
                      setFilters({...filters, empresa: value});
                      resetPagination();
                    }}
                  >
                    <SelectTrigger className="mt-1">
                      <SelectValue placeholder="Todas as empresas" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todas as empresas</SelectItem>
                      {companyOptions.map((company) => (
                        <SelectItem key={company.value} value={company.value}>
                          {company.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="flex gap-2 mt-4">
                <Button
                  variant="outline"
                  onClick={clearFilters}
                  size="sm"
                >
                  <X className="h-4 w-4 mr-2" />
                  Limpar Filtros
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Pagination Controls */}
      <Card>
        <CardContent className="pt-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-600">Funcionários por página:</span>
              <Select
                value={itemsPerPage.toString()}
                onValueChange={(value) => {
                  setItemsPerPage(parseInt(value));
                  setCurrentPage(1);
                }}
              >
                <SelectTrigger className="w-20">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="10">10</SelectItem>
                  <SelectItem value="20">20</SelectItem>
                  <SelectItem value="30">30</SelectItem>
                  <SelectItem value="50">50</SelectItem>
                </SelectContent>
              </Select>
              <span className="text-sm text-gray-600">
                Mostrando {startIndex + 1} a {Math.min(endIndex, filteredEmployees.length)} de {filteredEmployees.length} funcionários
              </span>
            </div>

            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(currentPage - 1)}
                disabled={currentPage === 1}
              >
                Anterior
              </Button>

              <div className="flex space-x-1">
                {Array.from({ length: totalPages }, (_, i) => i + 1)
                  .filter((page) => {
                    const maxVisiblePages = 5;
                    const halfRange = Math.floor(maxVisiblePages / 2);
                    return page >= currentPage - halfRange && page <= currentPage + halfRange;
                  })
                  .map((page) => (
                    <Button
                      key={page}
                      variant={page === currentPage ? "default" : "outline"}
                      size="sm"
                      onClick={() => setCurrentPage(page)}
                      className="w-8 h-8 p-0"
                    >
                      {page}
                    </Button>
                  ))}
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                Próxima
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Tabela Profissional de Funcionários */}
      <Card>
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg">Lista de Funcionários</CardTitle>
            <div className="text-sm text-gray-500">
              {employees.length} de {allEmployees?.length || 0} funcionários
            </div>
          </div>
        </CardHeader>

        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <Table>
              <TableHeader className="bg-gray-50">
                <TableRow>
                  <TableHead className="w-[80px] font-semibold">Empresa</TableHead>
                  <TableHead className="w-[120px] font-semibold">Matrícula</TableHead>
                  <TableHead className="font-semibold">Nome</TableHead>
                  <TableHead className="font-semibold">RG</TableHead>
                  <TableHead className="font-semibold">PIS</TableHead>
                  <TableHead className="font-semibold">Admissão</TableHead>
                  <TableHead className="font-semibold">Salário</TableHead>
                  <TableHead className="font-semibold">Cargo</TableHead>
                  <TableHead className="font-semibold">Centro de Custo</TableHead>
                  <TableHead className="w-[100px] font-semibold">Ações</TableHead>
                </TableRow>
              </TableHeader>

              <TableBody>
                {employees.map((employee) => (
                  <TableRow key={employee.id} className="hover:bg-gray-50">
                    <TableCell className="font-medium">
                      <Badge variant="outline" className="text-xs">
                        {employee.empresa || "N/A"}
                      </Badge>
                    </TableCell>
                    <TableCell className="font-medium text-blue-600">
                      {employee.matricula}
                    </TableCell>
                    <TableCell className="font-medium">
                      {employee.nome}
                    </TableCell>
                    <TableCell className="text-gray-600">
                      {employee.rg || "-"}
                    </TableCell>
                    <TableCell className="text-gray-600">
                      {employee.pis || "-"}
                    </TableCell>
                    <TableCell>
                      {formatDate(employee.dataAdmissao)}
                    </TableCell>
                    <TableCell className="font-medium">
                      {formatSalaryDisplay(employee.salario)}
                    </TableCell>
                    <TableCell>
                      {employee.cargo || "-"}
                    </TableCell>
                    <TableCell>
                      {employee.centroCusto || "-"}
                    </TableCell>
                    <TableCell>
                      <div className="flex gap-1">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => openEditModal(employee)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>

                        <AlertDialog>
                          <AlertDialogTrigger asChild>
                            <Button variant="ghost" size="sm" className="text-red-600 hover:text-red-700">
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </AlertDialogTrigger>
                          <AlertDialogContent>
                            <AlertDialogHeader>
                              <AlertDialogTitle>Confirmar exclusão</AlertDialogTitle>
                              <AlertDialogDescription>
                                Tem certeza que deseja excluir o funcionário <strong>{employee.nome}</strong>?
                                Esta ação não pode ser desfeita.
                              </AlertDialogDescription>
                            </AlertDialogHeader>
                            <AlertDialogFooter>
                              <AlertDialogCancel>Cancelar</AlertDialogCancel>
                              <AlertDialogAction 
                                onClick={() => deleteEmployeeMutation.mutate(employee.id)}
                                className="bg-red-600 hover:bg-red-700"
                              >
                                Excluir
                              </AlertDialogAction>
                            </AlertDialogFooter>
                          </AlertDialogContent>
                        </AlertDialog>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>

            {employees.length === 0 && (
              <div className="text-center py-8">
                <User className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">Nenhum funcionário encontrado</p>
                <p className="text-sm text-gray-400">Tente ajustar os filtros ou adicionar novos funcionários</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Modal de Criar/Editar Funcionário */}
      <Dialog open={isCreateModalOpen || isEditModalOpen} onOpenChange={(open) => {
        if (!open) {
          setIsCreateModalOpen(false);
          setIsEditModalOpen(false);
          resetForm();
        }
      }}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {selectedEmployee ? 'Editar Funcionário' : 'Novo Funcionário'}
            </DialogTitle>
            <DialogDescription>
              {selectedEmployee ? 'Altere os dados do funcionário selecionado.' : 'Preencha os dados do novo funcionário.'}
            </DialogDescription>
          </DialogHeader>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 py-4">
            <div>
              <Label htmlFor="empresa">Empresa *</Label>
              <Select
                value={formData.empresa}
                onValueChange={(value) => setFormData({...formData, empresa: value})}
              >
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Selecione a empresa" />
                </SelectTrigger>
                <SelectContent>
                  {companyOptions.map((company) => (
                    <SelectItem key={company.value} value={company.value}>
                      {company.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="nome">Nome *</Label>
              <Input
                id="nome"
                value={formData.nome}
                onChange={(e) => setFormData({...formData, nome: e.target.value})}
                className="mt-1"
                placeholder="Ex: Lucas Silva"
                required
              />
            </div>

            <div>
              <Label htmlFor="matricula">Matrícula *</Label>
              <Input
                id="matricula"
                value={formData.matricula}
                onChange={(e) => setFormData({...formData, matricula: e.target.value})}
                className="mt-1"
                required
              />
            </div>

            <div>
              <Label htmlFor="rg">RG</Label>
              <Input
                id="rg"
                value={formData.rg}
                onChange={(e) => setFormData({...formData, rg: e.target.value})}
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="pis">PIS</Label>
              <Input
                id="pis"
                value={formData.pis}
                onChange={(e) => setFormData({...formData, pis: e.target.value})}
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="dataAdmissao">Data Admissão</Label>
              <Input
                id="dataAdmissao"
                type="date"
                value={formData.dataAdmissao}
                onChange={(e) => setFormData({...formData, dataAdmissao: e.target.value})}
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="dataDemissao">Data Demissão</Label>
              <Input
                id="dataDemissao"
                type="date"
                value={formData.dataDemissao}
                onChange={(e) => setFormData({...formData, dataDemissao: e.target.value})}
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="salario">Salário</Label>
              <Input
                id="salario"
                value={formData.salario}
                onChange={handleSalaryChange}
                placeholder="Ex: 5000,00 ou 5000.50"
                className="mt-1"
              />
              <p className="text-xs text-gray-500 mt-1">Digite apenas números, vírgula ou ponto</p>
            </div>

            <div>
              <Label htmlFor="cargo">Cargo</Label>
              <Input
                id="cargo"
                value={formData.cargo}
                onChange={(e) => setFormData({...formData, cargo: e.target.value})}
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="centroCusto">Centro de Custo</Label>
              <Input
                id="centroCusto"
                value={formData.centroCusto}
                onChange={(e) => setFormData({...formData, centroCusto: e.target.value})}
                className="mt-1"
                placeholder="Ex: 001, 002, 003, etc."
              />
            </div>

            <div>
              <Label htmlFor="departamento">Departamento</Label>
              <Input
                id="departamento"
                value={formData.departamento}
                onChange={(e) => setFormData({...formData, departamento: e.target.value})}
                className="mt-1"
                placeholder="Ex: Financeiro, Operações, etc."
              />
            </div>


          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => {
              setIsCreateModalOpen(false);
              setIsEditModalOpen(false);
              resetForm();
            }}>
              Cancelar
            </Button>
            <Button 
              onClick={handleSubmit}
              disabled={createEmployeeMutation.isPending || updateEmployeeMutation.isPending}
            >
              {selectedEmployee ? 'Salvar Alterações' : 'Criar Funcionário'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

