  padding: 0.25rem 0.5rem;
}

@media (max-width: 768px) {
  .deadline-alert {
    font-size: 0.625rem;
    padding: 0.125rem 0.375rem;
  }
}

/* Filters card responsive improvements */
.filters-card {
  margin: 0.5rem;
}

@media (max-width: 640px) {
