/* Dragging state */
.react-grid-item.react-dragging {
  transition: none;
  z-index: 3;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

.react-grid-item.react-dragging .widget-container {
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
  transform: rotate(2deg);
}

/* Ensure full height usage */
.react-grid-layout,
.react-grid-item,
.widget-container {
  box-sizing: border-box;
}

/* Hide overflow on mobile to prevent horizontal scroll */
@media (max-width: 768px) {
  body {
    overflow-x: hidden;
  }

  .dashboard-container {
    overflow-x: hidden;
  }
}

/* Widget specific fixes */
.widget-container .recharts-wrapper {
  width: 100% !important;
  height: auto !important;
}

.widget-container table {
  width: 100%;
  table-layout: fixed;
}

.widget-container .stat-card {
  margin: 0;
  padding: 0;
}

/* Loading states */
.widget-loading {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  background: #f9fafb;
}

/* Error states */
.widget-error {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  background: #fef2f2;
  color: #dc2626;
  text-align: center;
  padding: 20px;
}
